import React, { useEffect, useState } from "react";
import axios from "axios";



function Todo() {

    const [itemText, setItemText] = useState('')
    const [listItems, setListItem] = useState([])
    const addItem = async (e) => {
        try {
            e.preventDefault();
            const res = await axios.post("http://localhost:5500/user/api/item", { item: itemText })
            console.log(res);
            setItemText('')
        } catch (err) {
            console.log(err)
        }
    }


    //create function to fetch all to items from database -- we wil use useEffect hook

    useEffect(() => {
        const getItemList = async () => {
            try {
                const res = await axios.get("http://localhost:5500/user/api/items")
                setListItem(res.data)

            } catch (err) {
                console.log(err)
            }
        }
        getItemList();
    }, [])


    return (
        <div className="container" onSubmit={e => addItem(e)}>
            <h1>Todo List</h1>
            <form className="form" >
                <input type="text" placeholder="Add Todo Item" onChange={e => { setItemText(e.target.value) }} value={itemText} />
                <button type="submit" >Add</button>
            </form>
            <div className="todo-listItems" >
                {
                    listItems
                }




                {/* <div className="todo-item" >
